  `id` int(4) NOT NULL,
  `rightAnswer` int(2) NOT NULL,
  `alternativeA` varchar(500) NOT NULL,
  `alternativeB` varchar(500) NOT NULL,
  `alternativeC` varchar(500) NOT NULL,
  `alternativeD` varchar(500) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

