--
-- Estrutura da tabela `ranking_data`
--

CREATE TABLE `ranking` (
  `id` int(4) NOT NULL,
  `userId` int(4) NOT NULL,
  `points` int(6) NOT NULL DEFAULT '0',
  `level` int(3) NOT NULL DEFAULT '1',
  `needPoints` int(6) NOT NULL DEFAULT '1000',
  `difficulty` int(1) NOT NULL DEFAULT '1',
  `rightAnswers` int(4) NOT NULL DEFAULT '0',
  `totalAnswers` int(4) NOT NULL DEFAULT '0'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

